typedef struct f_config {
    char *userid;
    char *passwd;
    char *url;
    char *error_str;
    int   sso;
} f_config;

typedef struct f_attr {
    char *name;
    char **values;
    int size;
} f_attr;

typedef struct f_attr_array {
    f_attr *attributes;
    int size;
} f_attr_array;
